+    overflow-y: auto;
+}
+
+/* Seção de categoria */
+.category-section {
+    margin-bottom: 40px;
+}
+
+.category-section:last-child {
+    margin-bottom: 0;
+}
+
+/* Título da categoria */
+.category-title {
+    font-size: 1.3rem;
+    font-weight: bold;
+    margin-bottom: 15px;
+    color: var(--text-color);
+    display: flex;
+    align-items: center;
+    gap: 8px;
+}
+
+/* Grid de filmes da categoria */
+.category-grid {
+    display: grid;
+    grid-template-columns: repeat(auto-fill, minmax(120px, 1fr));
+    gap: 12px;
+    margin-bottom: 15px;
+}
+
+/* Card de filme na categoria */
+.category-movie-card {
+    background-color: var(--card-bg);
+    border-radius: 8px;
+    overflow: hidden;
+    cursor: pointer;
+    transition: var(--transition);
+    border: 2px solid transparent;
+    position: relative;
+    height: 180px;
+    display: flex;
+    flex-direction: column;
+}
+
+.category-movie-card:hover {
+    transform: translateY(-5px);
+    border-color: var(--primary-color);
+    box-shadow: 0 8px 20px rgba(229, 9, 20, 0.3);
+}
+
+/* Poster do filme na categoria */
+.category-movie-poster {
+    width: 100%;
+    height: 140px;
+    object-fit: cover;
+    background-color: var(--border-color);
+}
+
+/* Informações do filme na categoria */
+.category-movie-info {
+    padding: 8px;
+    flex: 1;
+    display: flex;
+    flex-direction: column;
+    justify-content: flex-end;
+}
+
+.category-movie-title {
+    font-size: 0.75rem;
+    font-weight: bold;
+    white-space: nowrap;
+    overflow: hidden;
+    text-overflow: ellipsis;
+    margin-bottom: 2px;
+    color: var(--text-color);
+}
+
+.category-movie-year {
+    font-size: 0.65rem;
+    color: #888;
+}
+
+/* Filmes ocultos */
+.hidden-movies {
+    transition: var(--transition);
+}
+
+/* Botão Mostrar Mais */
+.show-more-btn {
+    width: 100%;
+    padding: 12px;
+    background-color: var(--primary-color);
+    color: white;
+    border: none;
+    border-radius: 6px;
+    cursor: pointer;
+    font-weight: bold;
+    font-size: 0.95rem;
+    transition: var(--transition);
+    margin-top: 10px;
+}
+
+.show-more-btn:hover {
+    background-color: #c40812;
+    transform: translateY(-2px);
+}
+
+.show-more-btn:active {
+    transform: translateY(0);
+}
+
+/* ============================================
+   RESPONSIVIDADE PARA CATEGORIAS
+   ============================================ */
+
+@media (max-width: 768px) {
