+    }
+
+    .category-movie-card {
+        height: 140px;
+    }
+
+    .category-movie-poster {
+        height: 100px;
+    }
+
+    .category-movie-title {
+        font-size: 0.65rem;
+    }
+
+    .category-movie-year {
+        font-size: 0.55rem;
+    }
+
+    .show-more-btn {
+        padding: 8px;
+        font-size: 0.85rem;
+    }
+}
+
+/* ============================================
+   AJUSTES DO MODAL PARA COMPORTAR CATEGORIAS
+   ============================================ */
+
+.modal-content {
+    max-height: 95vh;
+    overflow-y: auto;
+}
+
+.modal-body {
+    display: grid;
+    grid-template-columns: 1fr 1fr;
+    gap: 30px;
+    padding: 30px;
+}
+
+/* Scroll suave para o modal */
+.modal-content::-webkit-scrollbar {
+    width: 8px;
+}
+
+.modal-content::-webkit-scrollbar-track {
+    background: var(--card-bg);
+    border-radius: 10px;
+}
+
+.modal-content::-webkit-scrollbar-thumb {
+    background: var(--primary-color);
+    border-radius: 10px;
+}
+
+.modal-content::-webkit-scrollbar-thumb:hover {
+    background: #c40812;
+}
+
