commits.content:
header {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 1.5rem 2rem;
    background: linear-gradient(90deg, #c41e3a 0%, #8b1a2e 100%);
    border-bottom: 3px solid #000;
    gap: 2rem;
    position: relative;
}

.header-logo {
    position: absolute;
    left: 2rem;
    flex-shrink: 0;
    display: flex;
    align-items: center;
    justify-content: flex-start;
}

.header-logo img {
    max-width: 80px;
    height: auto;
    object-fit: contain;
}

.header-content {
    flex: 1;
    text-align: center;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}

.header-title {
    font-size: 3rem;
    font-weight: bold;
    color: white;
    margin: 0;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
}

.header-subtitle {
    font-size: 1rem;
    color: #e0e0e0;
    margin: 0.5rem 0 0 0;
    text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5);
}

/* Responsividade */

@media (max-width: 768px) {
    .header {
        flex-direction: column;
        padding: 1rem;
        gap: 1rem;
        position: relative;
    }

    .header-logo {
        position: static;
        margin-bottom: 0.5rem;
    }

    .header-title {
        font-size: 2rem;
    }

    .header-subtitle {
        font-size: 0.9rem;
    }

    .header-logo img {
        max-width: 60px;
    }
}

@media (max-width: 480px) {
    .header {
        padding: 0.75rem;
    }

    .header-logo {
        position: static;
    }

    .header-title {
        font-size: 1.5rem;
    }

    .header-subtitle {
        font-size: 0.8rem;
    }

    .header-logo img {
        max-width: 50px;
    }
}
