+/**
+ * Busca filmes na API EmbedMovies
+ * @param {string} query 
+ * @returns {Promise<Array>} 
+ */
+async function searchMovies(query) {
+    try {
+        showLoading(true);
+        hideError();
+        const movies = await fetchMoviesFromEmbedAPI(query);
+
+        appState.allMovies = movies;
+        appState.currentPage = 1;
+        filterMovies();
+        displayMovies();
