+// ============================================
+// FUNÇÕES DE CATEGORIZAÇÃO
+// ============================================
+
+/**
+ * Obtém filmes de uma categoria específica
+ * @param {string} category - Tipo de categoria (releases, popular, classics, suggestions)
+ * @param {Array} movies - Array de filmes para filtrar
+ * @returns {Array}
+ */
+function getCategoryMovies(category, movies = appState.allMovies) {
+    const shuffled = [...movies].sort(() => Math.random() - 0.5);
+    
+    switch(category) {
+        case 'releases':
+            // Lançamentos: filmes mais recentes
+            return shuffled.sort((a, b) => parseInt(b.year) - parseInt(a.year)).slice(0, 12);
+        
+        case 'popular':
+            // Mais Assistidos: filmes com maior rating
+            return shuffled.sort((a, b) => {
+                const ratingA = parseFloat(a.rating) || 0;
+                const ratingB = parseFloat(b.rating) || 0;
+                return ratingB - ratingA;
+            }).slice(0, 12);
+        
+        case 'classics':
+            // Clássicos: filmes antigos (antes de 2000)
+            return shuffled.filter(m => parseInt(m.year) < 2000).slice(0, 12);
+        
+        case 'suggestions':
+            // Sugestões: seleção aleatória
+            return shuffled.slice(0, 12);
+        
+        default:
+            return shuffled.slice(0, 12);
+    }
+}
+
+/**
+ * Cria o HTML para uma categoria com 6 itens visíveis
+ * @param {string} categoryName - Nome da categoria (Lançamentos, Mais Assistidos, etc)
+ * @param {string} categoryId - ID da categoria (releases, popular, etc)
+ * @param {Array} movies - Array de filmes
+ * @returns {string}
+ */
+function createCategorySection(categoryName, categoryId, movies) {
+    const categoryMovies = getCategoryMovies(categoryId, movies);
+    const visibleMovies = categoryMovies.slice(0, 6);
+    const hiddenMovies = categoryMovies.slice(6);
+    const hasMore = hiddenMovies.length > 0;
+
+    let html = `
+        <div class="category-section" data-category="${categoryId}">
+            <h3 class="category-title">${categoryName}</h3>
+            <div class="category-grid">
+    `;
+
+    // Adiciona os 6 primeiros filmes
+    visibleMovies.forEach(movie => {
+        html += createCategoryMovieCard(movie);
+    });
+
+    html += `
+            </div>
+    `;
+
+    // Adiciona os filmes ocultos
+    if (hasMore) {
+        html += `
+            <div class="hidden-movies" id="hidden-${categoryId}" style="display: none;">
+                <div class="category-grid">
+        `;
+        
+        hiddenMovies.forEach(movie => {
+            html += createCategoryMovieCard(movie);
+        });
+
+        html += `
+                </div>
+            </div>
+            <button class="show-more-btn" onclick="toggleCategory('${categoryId}')">
+                Mostrar Mais (${hiddenMovies.length} filmes)
+            </button>
+        `;
+    }
+
+    html += `</div>`;
+    return html;
+}
