+    const typeLabel = movie.type === 'series' ? 'Série' : 'Filme';
+    return `
+        <div class="category-movie-card" data-movie-id="${movie.id}" onclick="openMovieFromCategory(event, ${movie.id})">
+            <img src="${movie.poster}" alt="${movie.title}" class="category-movie-poster" onerror="this.src='data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%22150%22 height=%22225%22%3E%3Crect fill=%22%23404040%22 width=%22150%22 height=%22225%22/%3E%3Ctext x=%2250%25%22 y=%2250%25%22 dominant-baseline=%22middle%22 text-anchor=%22middle%22 font-family=%22Arial%22 font-size=%2214%22 fill=%22%23888%22%3ESem Imagem%3C/text%3E%3C/svg%3E'">
+            <div class="category-movie-info">
+                <p class="category-movie-title">${movie.title}</p>
+                <p class="category-movie-year">${movie.year}</p>
+            </div>
+        </div>
+    `;
+}
+
+/**
+ * Alterna a exibição de filmes ocultos em uma categoria
+ * @param {string} categoryId - ID da categoria
+ */
+function toggleCategory(categoryId) {
+    const hiddenMovies = document.getElementById(`hidden-${categoryId}`);
+    const btn = event.target;
+    
+    if (hiddenMovies.style.display === 'none') {
+        hiddenMovies.style.display = 'block';
+        btn.textContent = 'Mostrar Menos';
+    } else {
+        hiddenMovies.style.display = 'none';
+        const categorySection = btn.closest('.category-section');
+        const movies = getCategoryMovies(categoryId);
+        const hiddenCount = movies.length - 6;
+        btn.textContent = `Mostrar Mais (${hiddenCount} filmes)`;
+    }
+}
