+        const dados = await resposta.json();
+        
+        if (dados.Response === 'False') {
+            throw new Error(dados.Error);
+        }
+        
+        // Buscar temporadas se for série
+        let temporadas = [];
+        if (dados.Type === 'series' && temporadasData[imdbId]) {
+            temporadas = temporadasData[imdbId];
