+function getCategoryMovies(category, type) {
+    const movies = appState.allMovies.filter(m => type === 'all' || m.type === type);
+    const shuffled = [...movies].sort(() => Math.random() - 0.5);
+    switch (category) {
+        case 'releases': return shuffled.sort((a,b)=>b.year-a.year);
