+++ 
@@ -474,4 +474,318 @@
     .pagination-btn {
         width: 100%;
     }
 }
+.modal-categories {
+    padding: 30px;
+    border-top: 2px solid var(--border-color);
+    max-height: 600px;
+    overflow-y: auto;
+}
+
+/* Seção de categoria */
+.category-section {
+    margin-bottom: 40px;
+}
+
+.category-section:last-child {
+    margin-bottom: 0;
+}
+
+/* Título da categoria */
+.category-title {
+    font-size: 1.3rem;
+    font-weight: bold;
+    margin-bottom: 15px;
+    color: var(--text-color);
+    display: flex;
+    align-items: center;
+    gap: 8px;
+}
+
+/* Grid de filmes da categoria */
+.category-grid {
+    display: grid;
+    grid-template-columns: repeat(auto-fill, minmax(120px, 1fr));
+    gap: 12px;
+    margin-bottom: 15px;
+}
+
+/* Card de filme na categoria */
+.category-movie-card {
+    background-color: var(--card-bg);
+    border-radius: 8px;
+    overflow: hidden;
+    cursor: pointer;
+    transition: var(--transition);
+    border: 2px solid transparent;
+    position: relative;
+    height: 180px;
+    display: flex;
+    flex-direction: column;
+}
+
+.category-movie-card:hover {
+    transform: translateY(-5px);
+    border-color: var(--primary-color);
+    box-shadow: 0 8px 20px rgba(229, 9, 20, 0.3);
+}
+
+/* Poster do filme na categoria */
+.category-movie-poster {
+    width: 100%;
+    height: 140px;
+    object-fit: cover;
+    background-color: var(--border-color);
+}
+
+/* Informações do filme na categoria */
+.category-movie-info {
+    padding: 8px;
+    flex: 1;
+    display: flex;
+    flex-direction: column;
+    justify-content: flex-end;
+}
+
+.category-movie-title {
+    font-size: 0.75rem;
+    font-weight: bold;
+    white-space: nowrap;
+    overflow: hidden;
+    text-overflow: ellipsis;
+    margin-bottom: 2px;
+    color: var(--text-color);
+}
+
+.category-movie-year {
+    font-size: 0.65rem;
+    color: #888;
+}
+
+/* Filmes ocultos */
+.hidden-movies {
+    transition: var(--transition);
+}
+
+/* Botão Mostrar Mais */
+.show-more-btn {
+    width: 100%;
+    padding: 12px;
+    background-color: var(--primary-color);
+    color: white;
+    border: none;
+    border-radius: 6px;
+    cursor: pointer;
+    font-weight: bold;
+    font-size: 0.95rem;
+    transition: var(--transition);
+    margin-top: 10px;
+}
+
+.show-more-btn:hover {
+    background-color: #c40812;
+    transform: translateY(-2px);
+}
+
+.show-more-btn:active {
+    transform: translateY(0);
+}
+
+/* ============================================
+   RESPONSIVIDADE PARA CATEGORIAS
+   ============================================ */
+
+@media (max-width: 768px) {
+    .modal-categories {
+        padding: 20px;
+        max-height: 500px;
+    }
+
+    .category-title {
+        font-size: 1.1rem;
+    }
+
+    .category-grid {
+        grid-template-columns: repeat(auto-fill, minmax(100px, 1fr));
+        gap: 10px;
+    }
+
+    .category-movie-card {
+        height: 160px;
+    }
+
+    .category-movie-poster {
+        height: 120px;
+    }
+
+    .category-movie-title {
+        font-size: 0.7rem;
+    }
+
+    .category-movie-year {
+        font-size: 0.6rem;
+    }
+
+    .show-more-btn {
+        padding: 10px;
+        font-size: 0.9rem;
+    }
+}
+
+@media (max-width: 480px) {
+    .modal-categories {
+        padding: 15px;
+        max-height: 400px;
+    }
+
+    .category-title {
+        font-size: 1rem;
+    }
+
+    .category-grid {
+        grid-template-columns: repeat(auto-fill, minmax(80px, 1fr));
+        gap: 8px;
+    }
+
+    .category-movie-card {
+        height: 140px;
+    }
+
+    .category-movie-poster {
+        height: 100px;
+    }
+
+    .category-movie-title {
+        font-size: 0.65rem;
+    }
+
+    .category-movie-year {
+        font-size: 0.55rem;
+    }
+
+    .show-more-btn {
+        padding: 8px;
+        font-size: 0.85rem;
+    }
+}
+
+/* ============================================
+   AJUSTES DO MODAL PARA COMPORTAR CATEGORIAS
+   ============================================ */
+
+.modal-content {
+    max-height: 95vh;
+    overflow-y: auto;
+}
+
+.modal-body {
+    display: grid;
+    grid-template-columns: 1fr 1fr;
+    gap: 30px;
+    padding: 30px;
+}
+
+/* Scroll suave para o modal */
+.modal-content::-webkit-scrollbar {
+    width: 8px;
+}
+
+.modal-content::-webkit-scrollbar-track {
+    background: var(--card-bg);
+    border-radius: 10px;
+}
+
+.modal-content::-webkit-scrollbar-thumb {
+    background: var(--primary-color);
+    border-radius: 10px;
+}
+
+.modal-content::-webkit-scrollbar-thumb:hover {
+    background: #c40812;
+}
+
+/* Firefox */
+.modal-content {
+    scrollbar-color: var(--primary-color) var(--card-bg);
+    scrollbar-width: thin;
+}
+
+/* ============================================
+   ANIMAÇÕES
+   ============================================ */
+
+@keyframes slideDown {
+    from {
+        opacity: 0;
+        max-height: 0;
+    }
+    to {
+        opacity: 1;
+        max-height: 1000px;
+    }
+}
+
+.hidden-movies {
+    animation: slideDown 0.3s ease-out;
+}
+
+/* ============================================
+   LAYOUT RESPONSIVO DO MODAL
+   ============================================ */
+
+@media (max-width: 1024px) {
+    .modal-body {
+        grid-template-columns: 1fr;
+        gap: 20px;
+        padding: 20px;
+    }
+
+    .modal-poster {
+        max-width: 300px;
+        margin: 0 auto;
+    }
+}
+
+@media (max-width: 768px) {
+    .modal-body {
+        grid-template-columns: 1fr;
+        gap: 15px;
+        padding: 15px;
+    }
+
+    .modal-info h2 {
+        font-size: 1.5rem;
+    }
+
+    .modal-poster {
+        max-width: 250px;
+        margin: 0 auto;
+    }
+}
+
+@media (max-width: 480px) {
+    .modal-body {
+        grid-template-columns: 1fr;
+        gap: 10px;
+        padding: 10px;
+    }
+
+    .modal-info h2 {
+        font-size: 1.2rem;
+    }
+
+    .modal-poster {
+        max-width: 200px;
+        margin: 0 auto;
+    }
+
+    .modal-links {
+        flex-direction: column;
+        gap: 8px;
+    }
+
+    .link-btn {
+        padding: 10px 15px;
+        font-size: 0.9rem;
+    }
+}
\ No newline at end of file

