commits.content:
/* Estilos específicos para a página de detalhes */

main {
    /* Garante que o main da página de detalhes tenha um padding adequado */
    padding: 20px;
}

.detail-container {
    display: flex;
    gap: 40px;
    margin-top: 30px;
    color: #ffffff;
}

.poster-area {
    flex-shrink: 0; /* Não permite que o pôster encolha */
    width: 300px; /* Largura fixa para o pôster */
}

.detail-poster {
    width: 100%;
    height: auto;
    border-radius: 8px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.5);
}

.info-area {
    flex-grow: 1; /* Permite que a área de informações ocupe o espaço restante */
}

.detail-title {
    font-size: 2.5em;
    margin-top: 0;
    margin-bottom: 10px;
    padding-left: 0; /* Remove o padding do h1/h2 do CSS principal */
}

.detail-metadata {
    font-size: 1.1em;
    color: #9ca3af; /* Cor de texto secundária */
    margin-bottom: 20px;
}

.rating {
    color: #facc15; /* Cor amarela para o rating */
    font-weight: bold;
}

.detail-synopsis {
    font-size: 1em;
    line-height: 1.6;
    margin-bottom: 30px;
}

.detail-cast h2 {
    font-size: 1.5em;
    margin-bottom: 10px;
    padding-left: 0;
}

.detail-cast ul {
    list-style: none;
    padding: 0;
    margin-bottom: 30px;
}

.detail-cast li {
    margin-bottom: 5px;
    color: #9ca3af;
}

.watch-button {
    background-color: #ef4444; /* Vermelho vibrante */
    color: #ffffff;
    border: none;
    padding: 15px 30px;
    font-size: 1.2em;
    font-weight: bold;
    border-radius: 8px;
    cursor: pointer;
    transition: background-color 0.3s;
}

.watch-button:hover {
    background-color: #dc2626;
}

.related-content h2 {
    font-size: 2em;
    margin-top: 50px;
    margin-bottom: 20px;
}

/* Media Query para telas menores */
@media (max-width: 768px) {
    .detail-container {
        flex-direction: column; /* Empilha o pôster e as informações */
        gap: 20px;
    }

    .poster-area {
        width: 100%;
        max-width: 300px; /* Limita a largura do pôster em telas menores */
        margin: 0 auto;
    }

    .detail-title {
        font-size: 2em;
    }
}
