+    color: var(--text-medium);
+    text-align: center;
+}
+
+.episode-link {
+    color: var(--text-light);
+    text-decoration: none;
+    font-size: 1em;
+}
+
+.episode-link:hover {
+    text-decoration: underline;
+}
+
+
+@media (max-width: 768px) {
+    .series-detail-container {
+        flex-direction: column;
+        gap: 20px;
+    }
+
+    .poster-area {
+        width: 100%;
+        max-width: 200px;
+        margin: 0 auto;
+    }
+
+    .rating-box {
+        flex-wrap: wrap;
+    }
+
+    .episode-item {
+        grid-template-columns: 60px 40px 1fr;
+    }
+}
