commits.content:
/* Estilos específicos para a página de detalhes da série */

/* Cor de fundo principal dos containers de informação, baseada na imagem */
:root {
    --series-bg-color: #33473D; /* Um verde/cinza escuro para os blocos */
    --accent-green: #4CAF50; /* Verde para botões e destaque */
    --text-light: #ffffff;
    --text-medium: #cccccc;
}

main {
    padding: 20px;
}

.series-detail-container {
    display: flex;
    gap: 40px;
    margin-top: 30px;
    color: var(--text-light);
    background-color: var(--series-bg-color); /* Fundo do container principal */
    padding: 20px;
    border-radius: 8px;
}

.poster-area {
    flex-shrink: 0;
    width: 200px; /* Ajustado para um tamanho mais comum de pôster */
}

.series-poster {
    width: 100%;
    height: auto;
    border-radius: 8px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.5);
}

.info-area {
    flex-grow: 1;
}

.series-title {
    font-size: 2em;
    margin-top: 0;
    margin-bottom: 5px;
    padding-left: 0;
}

.series-metadata {
    font-size: 0.9em;
    color: var(--text-medium);
    margin-bottom: 15px;
}

.platform {
    color: #0077c2; /* Cor azul para NETFLIX */
    font-weight: bold;
}

.rating-box {
    display: flex;
    align-items: center;
    gap: 15px;
    margin-bottom: 15px;
}

.rating-score {
    font-size: 2.5em;
    font-weight: bold;
    color: var(--text-light);
}

.stars {
    color: #facc15; /* Amarelo para as estrelas */
    font-size: 1.5em;
    letter-spacing: 2px;
}

.user-rating-button {
    background-color: transparent;
    border: 1px solid var(--text-medium);
    color: var(--text-medium);
    padding: 5px 10px;
    border-radius: 4px;
    cursor: pointer;
    font-size: 0.9em;
}

.series-genre {
    font-size: 1em;
    color: var(--text-medium);
    margin-bottom: 20px;
}

.nav-tabs {
    display: flex;
    gap: 10px;
    border-bottom: 1px solid #444;
    padding-bottom: 10px;
    margin-bottom: 20px;
}

.tab-button {
    background-color: transparent;
    border: none;
    color: var(--text-medium);
    padding: 8px 15px;
    cursor: pointer;
    font-size: 1em;
    transition: color 0.3s;
}

.tab-button.active {
    background-color: var(--accent-green);
    color: var(--text-light);
    border-radius: 4px;
}

.tab-button:not(.active):hover {
    color: var(--text-light);
}

/* Seção de Episódios */
.episodes-section h2 {
    font-size: 1.5em;
    padding-left: 0;
    margin-top: 30px;
    margin-bottom: 15px;
}

.season-item {
    margin-bottom: 5px;
}

.season-header {
    display: flex;
    align-items: center;
    background-color: var(--series-bg-color);
    color: var(--text-light);
    padding: 15px 20px;
    border-radius: 4px;
    cursor: pointer;
    transition: background-color 0.2s;
}

.season-header:hover {
    background-color: #445b50;
}

.season-number {
    font-size: 1.5em;
    font-weight: bold;
    background-color: var(--accent-green);
    padding: 5px 15px;
    border-radius: 4px;
    margin-right: 20px;
}

.season-title {
    font-size: 1.2em;
    font-weight: bold;
    flex-grow: 1;
}

.season-date {
    font-size: 0.9em;
    color: var(--text-medium);
}

.episode-list {
    background-color: #2a3a33; /* Cor de fundo para a lista de episódios */
    padding: 10px 0;
    border-radius: 0 0 4px 4px;
}

.episode-item {
    display: grid;
    grid-template-columns: 80px 50px 1fr; /* Thumb, Número, Título */
    align-items: center;
    padding: 10px 20px;
    border-bottom: 1px solid #445b50;
}

.episode-item:last-child {
    border-bottom: none;
}

.episode-item img {
    width: 100%;
    height: auto;
    border-radius: 4px;
}

.episode-number {
    font-size: 0.9em;
    color: var(--text-medium);
    text-align: center;
}

.episode-link {
    color: var(--text-light);
    text-decoration: none;
    font-size: 1em;
}

.episode-link:hover {
    text-decoration: underline;
}

/* Media Query para telas menores */
@media (max-width: 768px) {
    .series-detail-container {
        flex-direction: column;
        gap: 20px;
    }

    .poster-area {
        width: 100%;
        max-width: 200px;
        margin: 0 auto;
    }

    .rating-box {
        flex-wrap: wrap;
    }

    .episode-item {
        grid-template-columns: 60px 40px 1fr;
    }
}
