{
  // 1. Define o Prettier como o formatador padrão para todos os arquivos
  "editor.defaultFormatter": "esbenp.prettier-vscode",
  
  // 2. Formata o arquivo automaticamente toda vez que você salva
  "editor.formatOnSave": true,
  
  // 3. Diz ao ESLint para validar arquivos JavaScript, React e HTML
  "eslint.validate": [
    "javascript",
    "javascriptreact",
    "html"
  ]
}
